import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// react plugin used to create charts
import {Line, Bar} from "react-chartjs-2";

// reactstrap components
import {
    Button,
    ButtonGroup,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    UncontrolledDropdown,
    Label,
    FormGroup,
    Input,
    Table,
    Row,
    Col,
    UncontrolledTooltip
} from "reactstrap";

// core components
import {
    chartExample1,
    chartExample2,
    chartExample3,
    chartExample4
} from "variables/charts.js";
import axios from "axios";

const REST_HOST = process.env.REACT_APP_REST_HOST

class Dashboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            bigChartData: "data1",
            labels: [],
            dataset_data: []

        };
    }

    componentDidMount() {
        this.loadData();
    }

    loadData = async () => {
        const latitude = 39.91177;
        const longitude = 8.43568;
        const hours = 10;
        const href = `${REST_HOST}/air/history/lat=${latitude}&long=${longitude}&hours=${hours}`;
        const {data} = await axios.get(href);

        const historical = data.temporal_conditions;
        const historical_data = [];
        historical.map((value) => {
            historical_data.push({
                date: new Date(value.date).toLocaleString('pt-PT', {
                    'hour12': false,
                }),
                air_score: value.air_score
            })
        });

        const labels = [];
        const dataset_data = [];
        historical_data.map((value) => {
            labels.push(value.date);
            dataset_data.push(value.air_score);
        });


        await this.setState({
                labels: labels,
                dataset_data: dataset_data
            }
        );

    };

    fullData = canvas => {
        let ctx = canvas.getContext("2d");

        let gradientStroke = ctx.createLinearGradient(0, 230, 0, 50);

        gradientStroke.addColorStop(1, "rgba(29,140,248,0.2)");
        gradientStroke.addColorStop(0.4, "rgba(29,140,248,0.0)");
        gradientStroke.addColorStop(0, "rgba(29,140,248,0)"); //blue colors

        const dataSize = this.state.dataset_data.length;

        const historical_data = [];
        const forecast_data = [];
        for(let i = 0 ; i < dataSize / 2; i++) {
            historical_data.push(null);
        }

        return {
            labels: this.state.labels,
            datasets: [
                {
                    label: "Air Score",
                    fill: true,
                    backgroundColor: gradientStroke,
                    borderColor: "#1f8ef1",
                    borderWidth: 2,
                    borderDash: [],
                    borderDashOffset: 0.0,
                    pointBackgroundColor: "#1f8ef1",
                    pointBorderColor: "rgba(255,255,255,0)",
                    pointHoverBackgroundColor: "#1f8ef1",
                    pointBorderWidth: 20,
                    pointHoverRadius: 4,
                    pointHoverBorderWidth: 15,
                    pointRadius: 4,
                    data: this.state.dataset_data.slice(0,dataSize / 2)
                },
                {
                    label: "Air Score2",
                    fill: true,
                    backgroundColor: gradientStroke,
                    borderColor: "#1f8ef1",
                    borderWidth: 2,
                    borderDash: [],
                    borderDashOffset: 0.0,
                    pointBackgroundColor: "rgba(255,255,255,0)",
                    pointBorderColor: "rgba(255,255,255,0)",
                    pointHoverBackgroundColor: "#1f8ef1",
                    pointBorderWidth: 20,
                    pointHoverRadius: 4,
                    pointHoverBorderWidth: 15,
                    pointRadius: 4,
                    data: this.state.dataset_data.slice(dataSize / 2,dataSize+1)
                }
            ]
        };
    };


    setBgChartData = name => {
        this.setState({
            bigChartData: name
        });
    };


    elementClick = (e) => {
        console.log(e);
    };

    render() {
        return (
            <>
                <div className="content">
                    <Row>
                        <Col xs="12">
                            <Card className="card-chart">
                                <CardHeader>
                                    <Row>
                                        <Col className="text-left" sm="6">
                                            <h5 className="card-category">Air Condition</h5>
                                            <CardTitle tag="h2">Historical/Forecast</CardTitle>
                                        </Col>
                                        <Col sm="6">
                                            <ButtonGroup
                                                className="btn-group-toggle float-right"
                                                data-toggle="buttons"
                                            >
                                                <Button
                                                    tag="label"
                                                    className={classNames("btn-simple", {
                                                        active: this.state.bigChartData === "data1"
                                                    })}
                                                    color="info"
                                                    id="0"
                                                    size="sm"
                                                    onClick={() => this.setBgChartData("data1")}
                                                >
                                                    <input
                                                        defaultChecked
                                                        className="d-none"
                                                        name="options"
                                                        type="radio"
                                                    />
                                                    <span
                                                        className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Accounts
                          </span>
                                                    <span className="d-block d-sm-none">
                            <i className="tim-icons icon-single-02"/>
                          </span>
                                                </Button>
                                                <Button
                                                    color="info"
                                                    id="1"
                                                    size="sm"
                                                    tag="label"
                                                    className={classNames("btn-simple", {
                                                        active: this.state.bigChartData === "data2"
                                                    })}
                                                    onClick={() => this.setBgChartData("data2")}
                                                >
                                                    <input
                                                        className="d-none"
                                                        name="options"
                                                        type="radio"
                                                    />
                                                    <span
                                                        className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Purchases
                          </span>
                                                    <span className="d-block d-sm-none">
                            <i className="tim-icons icon-gift-2"/>
                          </span>
                                                </Button>
                                                <Button
                                                    color="info"
                                                    id="2"
                                                    size="sm"
                                                    tag="label"
                                                    className={classNames("btn-simple", {
                                                        active: this.state.bigChartData === "data3"
                                                    })}
                                                    onClick={() => this.setBgChartData("data3")}
                                                >
                                                    <input
                                                        className="d-none"
                                                        name="options"
                                                        type="radio"
                                                    />
                                                    <span
                                                        className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Sessions
                          </span>
                                                    <span className="d-block d-sm-none">
                            <i className="tim-icons icon-tap-02"/>
                                         </span>
                                                </Button>
                                            </ButtonGroup>
                                        </Col>
                                    </Row>
                                </CardHeader>
                                <CardBody>
                                    <div className="chart-area">
                                        <Line
                                            data={this.fullData}
                                            options={chartExample1.options}
                                            onElementsClick={this.elementClick}
                                        />
                                    </div>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </div>
            </>
        );
    }
}

export default Dashboard;
