import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// react plugin used to create charts
import {Line, Bar} from "react-chartjs-2";

// reactstrap components
import {
    Button,
    ButtonGroup,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    UncontrolledDropdown,
    Label,
    FormGroup,
    Input,
    Table,
    Row,
    Col,
    UncontrolledTooltip
} from "reactstrap";

// core components
import {
    chartExample1,
    chartExample2,
    chartExample3,
    chartExample4
} from "variables/charts.js";
import axios from "axios";

const REST_HOST = process.env.REACT_APP_REST_HOST

class Dashboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            bigChartData: "data1"
        };
    }

    componentDidMount() {
        this.loadData();
    }

    loadData = async () => {
        const latitude = 39.91177;
        const longitude = 8.43568;
        const hours = 1;
        const href = `${REST_HOST}/air/history/lat=${latitude}&long=${longitude}&hours=${hours}`;
        const {data} = await axios.get(href);

        const historical = data.temporal_conditions;
        const historical_data = [];
        historical.map((value) => {
            historical_data.push({
                date: new Date(value.date).toLocaleString('pt-PT', {
                    'hour12': false,
                    'dateStyle': 'short',
                    'timeStyle': 'short',
                    'formatMatcher' : 'best'
                }),
                air_score: value.air_score
            })
        });

        console.log(historical_data)
    };

    setBgChartData = name => {
        this.setState({
            bigChartData: name
        });
    };

    render() {
        return (
            <>
                <div className="content">
                    <Row>
                        <Col xs="12">
                            <Card className="card-chart">
                                <CardHeader>
                                    <Row>
                                        <Col className="text-left" sm="6">
                                            <h5 className="card-category">Air Condition</h5>
                                            <CardTitle tag="h2">Historical/Forecast</CardTitle>
                                        </Col>
                                        <Col sm="6">
                                            <ButtonGroup
                                                className="btn-group-toggle float-right"
                                                data-toggle="buttons"
                                            >
                                                <Button
                                                    tag="label"
                                                    className={classNames("btn-simple", {
                                                        active: this.state.bigChartData === "data1"
                                                    })}
                                                    color="info"
                                                    id="0"
                                                    size="sm"
                                                    onClick={() => this.setBgChartData("data1")}
                                                >
                                                    <input
                                                        defaultChecked
                                                        className="d-none"
                                                        name="options"
                                                        type="radio"
                                                    />
                                                    <span
                                                        className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Accounts
                          </span>
                                                    <span className="d-block d-sm-none">
                            <i className="tim-icons icon-single-02"/>
                          </span>
                                                </Button>
                                                <Button
                                                    color="info"
                                                    id="1"
                                                    size="sm"
                                                    tag="label"
                                                    className={classNames("btn-simple", {
                                                        active: this.state.bigChartData === "data2"
                                                    })}
                                                    onClick={() => this.setBgChartData("data2")}
                                                >
                                                    <input
                                                        className="d-none"
                                                        name="options"
                                                        type="radio"
                                                    />
                                                    <span
                                                        className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Purchases
                          </span>
                                                    <span className="d-block d-sm-none">
                            <i className="tim-icons icon-gift-2"/>
                          </span>
                                                </Button>
                                                <Button
                                                    color="info"
                                                    id="2"
                                                    size="sm"
                                                    tag="label"
                                                    className={classNames("btn-simple", {
                                                        active: this.state.bigChartData === "data3"
                                                    })}
                                                    onClick={() => this.setBgChartData("data3")}
                                                >
                                                    <input
                                                        className="d-none"
                                                        name="options"
                                                        type="radio"
                                                    />
                                                    <span
                                                        className="d-none d-sm-block d-md-block d-lg-block d-xl-block">
                            Sessions
                          </span>
                                                    <span className="d-block d-sm-none">
                            <i className="tim-icons icon-tap-02"/>
                          </span>
                                                </Button>
                                            </ButtonGroup>
                                        </Col>
                                    </Row>
                                </CardHeader>
                                <CardBody>
                                    <div className="chart-area">
                                        <Line
                                            data={chartExample1[this.state.bigChartData]}
                                            options={chartExample1.options}
                                        />
                                    </div>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                    <Row>
                        <Col lg="4">
                            <Card className="card-chart">
                                <CardHeader>
                                    <h5 className="card-category">Total Shipments</h5>
                                    <CardTitle tag="h3">
                                        <i className="tim-icons icon-bell-55 text-info"/>{" "}
                                        763,215
                                    </CardTitle>
                                </CardHeader>
                                <CardBody>
                                    <div className="chart-area">
                                        <Line
                                            data={chartExample2.data}
                                            options={chartExample2.options}
                                        />
                                    </div>
                                </CardBody>
                            </Card>
                        </Col>
                        <Col lg="4">
                            <Card className="card-chart">
                                <CardHeader>
                                    <h5 className="card-category">Daily Sales</h5>
                                    <CardTitle tag="h3">
                                        <i className="tim-icons icon-delivery-fast text-primary"/>{" "}
                                        3,500â‚¬
                                    </CardTitle>
                                </CardHeader>
                                <CardBody>
                                    <div className="chart-area">
                                        <Bar
                                            data={chartExample3.data}
                                            options={chartExample3.options}
                                        />
                                    </div>
                                </CardBody>
                            </Card>
                        </Col>
                        <Col lg="4">
                            <Card className="card-chart">
                                <CardHeader>
                                    <h5 className="card-category">Completed Tasks</h5>
                                    <CardTitle tag="h3">
                                        <i className="tim-icons icon-send text-success"/> 12,100K
                                    </CardTitle>
                                </CardHeader>
                                <CardBody>
                                    <div className="chart-area">
                                        <Line
                                            data={chartExample4.data}
                                            options={chartExample4.options}
                                        />
                                    </div>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </div>
            </>
        );
    }
}

export default Dashboard;
