import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// react plugin used to create charts
import {Line, Bar} from "react-chartjs-2";

// reactstrap components
import {
    Button,
    ButtonGroup,
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    UncontrolledDropdown,
    Label,
    FormGroup,
    Input,
    Table,
    Row,
    Col,
    UncontrolledTooltip, CardText, CardFooter
} from "reactstrap";

// core components
import {
    chartExample1,
    chartExample2,
    chartExample3,
    chartExample4
} from "variables/charts.js";
import axios from 'axios';

const REST_HOST = process.env.REACT_APP_REST_HOST

class Dashboard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            bigChartData: "data1",
            city_data: {
                cityName: '',
                cityId: '',
                cityLatitude: '',
                cityLongitude: '',
            },
            weatherData: {
                category: '',

            }
        };
    }

    setBgChartData = name => {
        this.setState({
            bigChartData: name
        });
    };

    get_air_quality_data = async () => {
        const city_data = JSON.parse(localStorage.getItem('city_data'));
        await this.setState({
            city_data: city_data
        });

        const latitude = this.state.city_data.cityLatitude;
        const longitude = this.state.city_data.cityLongitude;

        const href = `${REST_HOST}/air/lat=${latitude}&long=${longitude}`;

        const {data} = await axios.get(href);

        const current_air_condition = data.current_air_condition;

        const weatherData = {
            category: current_air_condition.category,
            color: current_air_condition.color
        }

        await this.setState({weatherData: weatherData});


    }


    componentDidMount() {


        this.get_air_quality_data();


    }

    render() {
        return (
            <>
                <div className="content">
                    <Row>
                        <Col md="12">
                            <Card className="card-user">
                                <CardBody>
                                    <CardText/>
                                    <div className="author">
                                        <div className="block block-one"/>
                                        <div className="block block-two"/>
                                        <div className="block block-three"/>
                                        <div className="block block-four"/>
                                        <a href="#pablo" onClick={e => e.preventDefault()}>
                                            <img
                                                alt="..."
                                                className="avatar"
                                                src={require("assets/img/emilyz.jpg")}
                                            />
                                            <h5 className="title">{this.state.city_data.cityName}</h5>
                                        </a>
                                        <p className="description"
                                           style={{color: "red"}}>{this.state.weatherData.category}</p>
                                    </div>
                                    <div className="card-description">
                                        Do not be scared of the truth because we need to restart the
                                        human foundation in truth And I love you like Kanye loves
                                        Kanye I love Rick Owens’ bed design but the back is...
                                    </div>
                                </CardBody>
                                <CardFooter>
                                    <div className="button-container">
                                        <Button className="btn-icon btn-round" color="facebook">
                                            <i className="fab fa-facebook"/>
                                        </Button>
                                        <Button className="btn-icon btn-round" color="twitter">
                                            <i className="fab fa-twitter"/>
                                        </Button>
                                        <Button className="btn-icon btn-round" color="google">
                                            <i className="fab fa-google-plus"/>
                                        </Button>
                                    </div>
                                </CardFooter>
                            </Card>
                        </Col>
                    </Row>
                </div>
            </>
        );
    }
}

export default Dashboard;
